import React, { useState, useEffect, useRef } from 'react';
import { NavLink, useNavigate, useLocation } from 'react-router-dom';
import ApiService from '../../service/ApiService';
import './'; // Fichier CSS s√©par√© pour les styles

function Navbar() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);
    const [userMenuOpen, setUserMenuOpen] = useState(false);
    const isAuthenticated = ApiService.isAuthenticated();
    const isAdmin = ApiService.isAdmin();
    const isUser = ApiService.isUser();
    const navigate = useNavigate();
    const location = useLocation();
    const userMenuRef = useRef(null);

    // Gestion du scroll pour l'effet de navbar transparente ‚Üí solide
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Fermer le menu utilisateur en cliquant √† l'ext√©rieur
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
                setUserMenuOpen(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleLogout = () => {
        const isLogout = window.confirm('Are you sure you want to logout?');
        if (isLogout) {
            ApiService.logout();
            navigate('/home');
            setIsMenuOpen(false);
            setUserMenuOpen(false);
        }
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleUserMenu = () => {
        setUserMenuOpen(!userMenuOpen);
    };

    const closeMenu = () => {
        setIsMenuOpen(false);
        setUserMenuOpen(false);
    };

    const getUserInitials = () => {
        // R√©cup√©rer les initiales de l'utilisateur (√† adapter selon votre impl√©mentation)
        const userName = ApiService.getCurrentUser()?.name || 'User';
        return userName.split(' ').map(n => n[0]).join('').toUpperCase();
    };

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''} ${isMenuOpen ? 'menu-open' : ''}`}>
            <div className="navbar-container">
                {/* Logo avec effet hover */}
                <div className="navbar-brand">
                    <NavLink to="/home" onClick={closeMenu}>
                        <span className="logo-icon">‚≠ê</span>
                        Star Hotel
                    </NavLink>
                </div>

                {/* Menu principal */}
                <ul className={`navbar-nav ${isMenuOpen ? 'active' : ''}`}>
                    <li>
                        <NavLink 
                            to="/home" 
                            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
                            onClick={closeMenu}
                        >
                            <span className="nav-icon">üè†</span>
                            Home
                        </NavLink>
                    </li>
                    <li>
                        <NavLink 
                            to="/rooms" 
                            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
                            onClick={closeMenu}
                        >
                            <span className="nav-icon">üõèÔ∏è</span>
                            Rooms
                        </NavLink>
                    </li>
                    <li>
                        <NavLink 
                            to="/find-booking" 
                            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
                            onClick={closeMenu}
                        >
                            <span className="nav-icon">üîç</span>
                            Find Booking
                        </NavLink>
                    </li>

                    {/* Menu utilisateur connect√© */}
                    {isAuthenticated && (
                        <>
                            {isUser && (
                                <li className="mobile-only">
                                    <NavLink 
                                        to="/profile" 
                                        className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
                                        onClick={closeMenu}
                                    >
                                        <span className="nav-icon">üë§</span>
                                        Profile
                                    </NavLink>
                                </li>
                            )}
                            {isAdmin && (
                                <li className="mobile-only">
                                    <NavLink 
                                        to="/admin" 
                                        className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
                                        onClick={closeMenu}
                                    >
                                        <span className="nav-icon">‚öôÔ∏è</span>
                                        Admin
                                    </NavLink>
                                </li>
                            )}
                        </>
                    )}
                </ul>

                {/* Section utilisateur */}
                <div className="navbar-user-section">
                    {!isAuthenticated ? (
                        <div className="auth-buttons">
                            <NavLink 
                                to="/login" 
                                className="login-btn"
                                onClick={closeMenu}
                            >
                                Login
                            </NavLink>
                            <NavLink 
                                to="/register" 
                                className="register-btn"
                                onClick={closeMenu}
                            >
                                Register
                            </NavLink>
                        </div>
                    ) : (
                        <div className="user-menu-container" ref={userMenuRef}>
                            <button 
                                className="user-avatar-btn"
                                onClick={toggleUserMenu}
                            >
                                <span className="user-avatar">
                                    {getUserInitials()}
                                </span>
                                <span className={`dropdown-arrow ${userMenuOpen ? 'open' : ''}`}>‚ñº</span>
                            </button>

                            {userMenuOpen && (
                                <div className="user-dropdown-menu">
                                    <div className="user-info">
                                        <span className="user-name">
                                            {ApiService.getCurrentUser()?.name || 'User'}
                                        </span>
                                        <span className="user-role">
                                            {isAdmin ? 'Administrator' : 'User'}
                                        </span>
                                    </div>

                                    <div className="dropdown-divider"></div>

                                    {isUser && (
                                        <NavLink 
                                            to="/profile" 
                                            className="dropdown-item"
                                            onClick={closeMenu}
                                        >
                                            <span className="dropdown-icon">üë§</span>
                                            Profile
                                        </NavLink>
                                    )}

                                    {isAdmin && (
                                        <NavLink 
                                            to="/admin" 
                                            className="dropdown-item"
                                            onClick={closeMenu}
                                        >
                                            <span className="dropdown-icon">‚öôÔ∏è</span>
                                            Admin Panel
                                        </NavLink>
                                    )}

                                    <button 
                                        className="dropdown-item logout-btn"
                                        onClick={handleLogout}
                                    >
                                        <span className="dropdown-icon">üö™</span>
                                        Logout
                                    </button>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                {/* Bouton menu mobile */}
                <button 
                    className={`hamburger-menu ${isMenuOpen ? 'active' : ''}`}
                    onClick={toggleMenu}
                    aria-label="Toggle menu"
                >
                    <span></span>
                    <span></span>
                    <span></span>
                </button>
            </div>
        </nav>
    );
}

export default Navbar;
